import { useState } from 'react';
import Offer from './offer'


function PurchaseHistory({ offers }) {

	const styleActive = {width: '100%'}
	const styleUnactive = {width: '25%'}

	const [activeStyle, setActivestyle] = useState(styleUnactive)

	return (
		<div onMouseEnter={() => {setActivestyle(styleActive)}} onMouseLeave={() => {setActivestyle(styleUnactive)}} className='purchaseHistory'>
			<div className='borderdDiv' style={activeStyle}>
				<h2>History</h2>
				<div style={{overflowX: activeStyle == styleActive ? 'hidden' : 'hidden'}} className='list'>
					{offers.map((offer) =>
						<span key={offer.id}>
							<Offer offer={offer} func={() => { console.log(offer) }} />
						</span>
					)}
				</div>
			</div>
		</div>
	);
}

export default PurchaseHistory;
