import { useState } from "react";

function CreateOffer({ }) {

	const [title, setTitle] = useState(null)
	const [description, setDescription] = useState(undefined)
	const [price, setPrice] = useState(undefined)
	const [picture, setPicture] = useState(undefined)


	async function createOfferRequest(e) {
		const message = {
			'name': title, 'description': description,
			'price': price, 'image': picture
		}

		const response = await fetch('/api/createOffer', {
			headers: {
				'Content-Type': 'application/json'
			},
			method: 'POST',
			body: JSON.stringify(message)
		})
	}

	return (
		<form onSubmit={createOfferRequest} className="createOffer borderdDiv">
			<h3>Name of the offer</h3>
			<input placeholder="Dog for sale..."></input>
			<h3>Description</h3>
			<input placeholder="This is a dog I am trying to get rid of..."></input>
			<h3>Picture</h3>
			<input placeholder="https://dolt.ee/api/gallery?img=busDog.jfif"></input>
			<h3>Price</h3>
			<div className="priceBox">
				<input placeholder="5"></input><span>â‚¬</span>
			</div>
			<button>Create Offer</button>
		</form>
	);
}

export default CreateOffer;
