import React, { useState, useEffect, useRef } from 'react';
import '../App.css';
import Header from '../components/header';

function getCookie(name) {
	const value = `; ${document.cookie}`;
	const parts = value.split(`; ${name}=`);
	if (parts.length === 2) return parts.pop().split(';').shift();
	return null;
}

function Admin() {
	const [isAuthenticated, setIsAuthenticated] = useState(false);
	const [loading, setLoading] = useState(true);

	const textareaRef = useRef(null)

	useEffect(() => {
		const checkToken = async () => {
			const token = getCookie('token');

			if (token) {
				try {
					const response = await fetch('/api/is_admin', {
						method: 'POST',
						headers: {
							'Content-Type': 'application/json',
						},
					});

					if (response.ok) {
						getJWT()
						var a = {}
						a.target = textareaRef.current
						takeSize(a)
						setIsAuthenticated(true);
					} else {
						setIsAuthenticated(false);
					}
				} catch (error) {
					console.error('Error validating token', error);
					setIsAuthenticated(false);
				}
			} else {
				setIsAuthenticated(false);
			}

			setLoading(false);
		};

		checkToken();
	}, []);

	async function getJWT() {
		const response = await fetch('/api/getAllOffers', {
			method: 'GET',
			headers: {
				'Content-Type': 'application/json',
			},
		});

		const message = await response.json()
		textareaRef.current.value = JSON.stringify(message, null, 2)
	}

	function takeSize(e) {
		var el = e.target;
		setTimeout(function () {
			el.style.cssText = 'min-height:37px; height: 37px; width: 800px';
			// for box-sizing other than "content-box" use:
			// el.style.cssText = '-moz-box-sizing:content-box';
			el.style.cssText = 'height:' + el.scrollHeight + 'px; width: 800px';
		}, 0);
	}

	if (loading) {
		return <div>Checking for admin status...</div>;
	}

	if (!isAuthenticated) {
		return <div>Page denied. You are not admin</div>;
	}

	return (
		<div className="Admin">
			<Header />
			<h1>Amazing, you truly are the admin of this page!</h1>

			<textarea style={{width: '800px'}} onChange={(e) => {
				takeSize(e)
			}} ref={textareaRef}></textarea>
		</div>
	);
}

export default Admin;
