import '../App.css';
import Offer from '../components/offer';
import { useEffect, useState } from 'react';
import Header from '../components/header';
import Showcase from '../components/showcase';

function Marketplace() {
  const [offers, setOffers] = useState([]);
  const [showcase, setShowcase] = useState(null);
  const [loadedOffers, setLoadedOffers] = useState([]); // Track which offers have loaded

  useEffect(() => {
    async function fetchOffers() {
      const response = await fetch('/api/getAllOffers', {
        method: 'GET',
      });
      const data = await response.json();
      setOffers(data);
    }
    fetchOffers();
  }, []);

  useEffect(() => {
    // Once the offers are loaded, set each offer's opacity to 1 with a staggered effect
    if (offers.length) {
      offers.forEach((offer, index) => {
        setTimeout(() => {
          setLoadedOffers((prev) => {
            const updatedLoadedOffers = [...prev];
            updatedLoadedOffers[index] = true; // Mark the current offer as loaded
            return updatedLoadedOffers;
          });
        }, 100 * index); // Staggered fade-in effect
      });
    }
  }, [offers]); // Trigger when offers are updated

  function closeShowcase() {
    setShowcase(null);
  }

  function showcaseOffer(offer) {
    setShowcase(offer);
  }

  return (
    <div className="Marketplace">
      <Header />
      <div className="scrollable">
        <Showcase offer={showcase} close={closeShowcase} />
        <div className="offerList">
          {offers.map((offer, index) => (
            <span key={offer.id}>
              <Offer
                offer={offer}
                func={showcaseOffer}
                style={{
                  opacity: loadedOffers[index] ? 1 : 0,
                  transition: 'opacity 500ms ease-in-out',
                }}
              />
            </span>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Marketplace;
